import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "\u6A94\u6848\u540D\u7A31\u70BA " + strings.filename(elem.src) + " \u7684\u5716\u50CF";

    case "TABLE":
      return "\u4EE5 " + strings.firstWords(elem.textContent, WORD_COUNT) + " \u958B\u59CB\u7684\u8868\u683C";

    case "A":
      return "\u6587\u5B57\u4EE5 " + strings.firstWords(elem.textContent, WORD_COUNT) + " \u958B\u59CB\u7684\u9023\u7D50";

    case "P":
      return "\u4EE5 " + strings.firstWords(elem.textContent, WORD_COUNT) + " \u958B\u59CB\u7684\u6BB5\u843D";

    case "TH":
      return "\u4EE5 " + strings.firstWords(elem.textContent, WORD_COUNT) + " \u958B\u59CB\u7684\u8868\u683C\u6A19\u984C";

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "\u4EE5 " + strings.firstWords(elem.textContent, WORD_COUNT) + " \u958B\u59CB\u7684\u6A19\u984C";

    default:
      return "\u4EE5 " + strings.firstWords(elem.textContent, WORD_COUNT) + " \u958B\u59CB\u7684\u5143\u7D20";
  }
}