import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "\u6587\u4EF6\u540D\u4E3A" + strings.filename(elem.src) + "\u7684\u56FE\u50CF";

    case "TABLE":
      return "\u4EE5" + strings.firstWords(elem.textContent, WORD_COUNT) + "\u5F00\u59CB\u7684\u8868\u683C";

    case "A":
      return "\u5177\u6709\u4EE5" + strings.firstWords(elem.textContent, WORD_COUNT) + "\u5F00\u59CB\u7684\u6587\u672C\u7684\u94FE\u63A5";

    case "P":
      return "\u4EE5" + strings.firstWords(elem.textContent, WORD_COUNT) + "\u5F00\u59CB\u7684\u6BB5\u843D";

    case "TH":
      return "\u4EE5" + strings.firstWords(elem.textContent, WORD_COUNT) + "\u5F00\u59CB\u7684\u8868\u683C\u6807\u9898";

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "\u4EE5" + strings.firstWords(elem.textContent, WORD_COUNT) + "\u5F00\u59CB\u7684\u6807\u9898";

    default:
      return "\u4EE5" + strings.firstWords(elem.textContent, WORD_COUNT) + "\u5F00\u59CB\u7684\u5143\u7D20";
  }
}